package com.assistant.drivingtest.db;public class ThirdSubjectItemTable {	public static final String TABLE_NAME = "third_subject_item";	public static final String CREATE_DB_TABLE = "CREATE TABLE IF NOT EXISTS "			+ TABLE_NAME + "(" + Columns._ID			+ " INTEGER PRIMARY KEY AUTOINCREMENT, " + Columns.SUBJECT_ID			+ " INTEGER, " + Columns.NAME + " VARCHAR, " + Columns.TYPE			+ " INTEGER, " + Columns.LONGITUDE + " VARCHAR, "			+ Columns.LATITUDE + " VARCHAR, " + Columns.VOICE + " VARCHAR, "			+ Columns.SPEED + " INTEGER " + ")";	public static final String DEL_TABLE = "DROP TABLE IF EXISTS " + TABLE_NAME;	public static class Columns {		public static final String _ID = "id";		public static final String SUBJECT_ID = "subject_id";		public static final String NAME = "name";		public static final String TYPE = "type";		public static final String LONGITUDE = "longitude";		public static final String LATITUDE = "latitude";		public static final String VOICE = "voice";		public static final String SPEED = "speed";	}}