package com.assistant.drivingtest.logic.test;import android.content.Context;import com.assistant.drivingtest.R;import com.assistant.drivingtest.domain.Deduction;import com.assistant.drivingtest.domain.ThirdTestItem;import com.assistant.drivingtest.logic.ThirdSubjectTestManager;import com.assistant.drivingtest.utils.Constant;import com.assistant.drivingtest.utils.MapUtil;import com.baidu.mapapi.map.LocationData;public class OvertakeTestObject extends TestObject {	public OvertakeTestObject(Context context,			ThirdSubjectTestManager testManager, ThirdTestItem item) {		super(context, testManager, item);		mKeepDirection = true;		checkDirection();	}	@Override	public void setAzimuth(double azimuth) {		if (azimuth > Constant.CHANGE_LINE) {			Deduction deduction = new Deduction();			deduction.name = mTestItem.name;			deduction.reason = mContext					.getString(R.string.deduction_message_overtake_right);			deduction.scores = -100;			mTestManager.testFail(deduction);		} else if (azimuth <= -Constant.CHANGE_LINE) {			if (mKeepDirection) {				Deduction deduction = new Deduction();				deduction.name = mTestItem.name;				deduction.reason = mContext						.getString(R.string.deduction_message_keep_direction);				deduction.scores = -10;				mTestManager.addDeduction(deduction);				mKeepDirection = false;			} else {				mTestManager.testSuccess();			}		}	}	@Override	public void setLocation(LocationData locationData) {		int dis = MapUtil.getDistanceInt(locationData.latitude,				locationData.longitude, mTestItem.voiceLatitude,				mTestItem.voiceLongitude);		int disEnd = MapUtil.getDistanceInt(locationData.latitude,				locationData.longitude, mTestItem.endLatitude,				mTestItem.endLongitude);		if (dis > Constant.DISTANCE_STANDARD || disEnd <= Constant.DISTANCE_OK) {			Deduction deduction = new Deduction();			deduction.name = mTestItem.name;			deduction.reason = mContext.getString(R.string.deduction_message);			deduction.scores = -100;			mTestManager.testFail(deduction);		}	}	@Override	public void setSpeed(double speed) {	}}