package com.assistant.drivingtest.logic.test;import android.content.Context;import android.os.Handler;import android.os.Message;import com.assistant.drivingtest.domain.Deduction;import com.assistant.drivingtest.domain.ThirdTestItem;import com.assistant.drivingtest.logic.ThirdSubjectTestManager;import com.baidu.mapapi.map.LocationData;public abstract class TestObject {	protected static final int SUCCESS = 0;	protected static final int FAIL = 1;	protected static final int KEEP_DIRECTION = 2;	protected ThirdSubjectTestManager mTestManager;	protected ThirdTestItem mTestItem;	protected boolean mStart = true;	protected boolean mKeepDirection;		protected boolean mIsManual;		protected LocationData mLocationData;	protected Handler mHandler = new Handler() {		public void handleMessage(Message msg) {			processMessage(msg);		};	};	protected Context mContext;	public TestObject(Context context, ThirdSubjectTestManager testManager,			ThirdTestItem item, boolean isManual) {		mContext = context;		mTestManager = testManager;		mTestItem = item;		mIsManual = isManual;	}	public abstract void setAzimuth(double azimuth);	public abstract void setLocation(LocationData locationData);	public abstract void setSpeed(double speed);	public void processMessage(Message message) {		switch (message.what) {		case SUCCESS:			mTestManager.testSuccess();			break;		case FAIL:			mTestManager.testFail((Deduction) message.obj);			break;		case KEEP_DIRECTION:			mKeepDirection = false;			break;		default:			break;		}	};	protected void checkDirection() {		mHandler.sendEmptyMessageDelayed(KEEP_DIRECTION, 3 * 1000);	}}