package com.assistant.drivingtest.logic.test;import android.content.Context;import com.assistant.drivingtest.R;import com.assistant.drivingtest.domain.Deduction;import com.assistant.drivingtest.domain.ThirdTestItem;import com.assistant.drivingtest.logic.ThirdSubjectTestManager;import com.assistant.drivingtest.utils.Constant;import com.assistant.drivingtest.utils.MapUtil;import com.baidu.mapapi.map.LocationData;public class TurnRightTestObject extends TestObject {	public TurnRightTestObject(Context context,			ThirdSubjectTestManager testManager, ThirdTestItem item) {		super(context, testManager, item);		Deduction deduction = new Deduction();		deduction.name = mTestItem.name;		deduction.reason = mContext.getString(R.string.deduction_message);		deduction.scores = -100;		mHandler.sendMessageDelayed(mHandler.obtainMessage(FAIL, deduction),				10 * 1000);	}	@Override	public void setAzimuth(double azimuth) {		if (azimuth > Constant.TURN_RIGHT) {			mHandler.removeMessages(FAIL);			mTestManager.testSuccess();		}	}	@Override	public void setLocation(LocationData locationData) {		int dis = MapUtil				.getDistanceInt(locationData.latitude, locationData.longitude,						mTestItem.latitude, mTestItem.longitude);		if (dis > Constant.DISTANCE_STANDARD) {			mHandler.removeMessages(FAIL);			Deduction deduction = new Deduction();			deduction.name = mTestItem.name;			deduction.reason = mContext.getString(R.string.deduction_message);			deduction.scores = -100;			mTestManager.testFail(deduction);		}	}	@Override	public void setSpeed(double speed) {	}}