package com.assistant.drivingtest.ui;import java.io.BufferedReader;import java.io.InputStream;import java.io.InputStreamReader;import com.assistant.drivingtest.R;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.TextView;public class CriteriaActivity extends Activity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.second_criteria);		Intent intent = getIntent();		int title = intent.getIntExtra("title", R.string.third_criteria_title);		int text = intent.getIntExtra("text", R.raw.third_criteria);		int color = intent.getIntExtra("color",				getResources().getColor(R.color.third_title_color));		TextView titleView = (TextView) findViewById(R.id.title);		TextView textView = (TextView) findViewById(R.id.text);		RelativeLayout titleLayout = (RelativeLayout) findViewById(R.id.title_color);		ImageView back = (ImageView) findViewById(R.id.back);		back.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				finish();			}		});		titleView.setText(title);		titleLayout.setBackgroundColor(color);		InputStream inputStream = getResources().openRawResource(text);		try {			BufferedReader reader = new BufferedReader(new InputStreamReader(					inputStream));			StringBuffer buffer = new StringBuffer();			String string;			while ((string = reader.readLine()) != null) {				buffer.append(string).append("\n");			}			reader.close();			textView.setText(buffer.toString());		} catch (Exception e) {			e.printStackTrace();		}	}}