package com.assistant.drivingtest.ui;import com.assistant.drivingtest.R;import com.assistant.drivingtest.logic.ThirdTestItemManager;import android.os.Bundle;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;public class NightDrivingFragment extends Fragment {	@Override	public View onCreateView(LayoutInflater inflater, ViewGroup container,			Bundle savedInstanceState) {		return inflater.inflate(R.layout.night_driving, container, false);	}	@Override	public void onViewCreated(View view, Bundle savedInstanceState) {		super.onViewCreated(view, savedInstanceState);		ImageView back = (ImageView) view.findViewById(R.id.back);		back.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View v) {				getActivity().finish();			}		});		ThirdTestItemManager thirdTestItemManager = ThirdTestItemManager				.getInstace();		thirdTestItemManager.startLightTest(getActivity());	}	@Override	public void onDestroy() {		super.onDestroy();		ThirdTestItemManager.getInstace().stopLightTest();	}}